service') ) LOGGER.debug('Run OSTF platform tests') test_class_main = ('fuel_health.tests.tests_platform' '.test_murano_linux.MuranoDeployLinuxServicesTests') tests_names = ['test_deploy_dummy_app', ] test_classes = [] for test_name in tests_names: test_classes.append('{0}.{1}'.format(test_class_main, test_name)) for test_name in test_classes: self.fuel_web.run_single_ostf_test( cluster_id=cluster_id, test_sets=['tests_platform'], test_name=test_name, timeout=60 * 20) LOGGER.debug('Running Murano external deployment test suite') cmd = ( "yum install git -y && " "rm -rf murano-tests && " "git clone https://github.com/Mirantis/murano-tests && " "echo 'export OS_USERNAME={0}
