put results print("Bandit jobs have been defined in the following OpenStack projects:") for project in sorted(bandit_projects): print(" - {0}".format(project)) print("
(Configuration from {0}{1}{2})
".format( BASE_URL, PATH_INFRA, conf_jenkins )) def coverage_zuul(conf_zuul): data = get_yaml("{0}{1}{2}".format(BASE_URL, PATH_INFRA, conf_zuul)) # parse data bandit_jobs = {} bandit_tests = {key: set() for key in TEST_TYPES} for job in data['jobs']: if 'bandit' in job['name']: if job.get('voting', True) is False: bandit_jobs[job['name']] = False else: bandit_jobs[job['name']] = True for project in data['projects']: project_name = project['name'] for test_type in bandit_tests.keys(): for test in project.get(test_type, []): if test.endswith('bandit'): voting = bandit_jobs.get(test, False) bandit_tests[test_type].add((project_name, voting)) # output results for test_type in bandit_tests: print( "
{0} tests exist for the following OpenStack projects:" .format(test_type.capitalize()) ) for project in sorted(bandit_tests[test_type]): if project[1] is False: print(" - {0}".format(project[0])) else: print(" - {0} (VOTING)".format(project[0])) print("
(Configuration from {0}{1}{2})
".format( BASE_URL, PATH_INFRA, conf_zuul )) def main(): print("{0}
{1}
{0}
".format( "=" * len(TITLE), TITLE, "=" * len(TITLE) )) coverage_jenkins(PATH_JENKINS) coverage_zuul(PATH_ZUUL) print("=" * len(TITLE)) if __name__ == "__main__": main()

