 def _log_diff_call(self, method_name, context): # On the delete the context.current item is the object with # all the info and the context.original is the delete object # In order to have the output format correct this swap is needed if "delete" in method_name: og_item = context.current if context.current else {} curr_item = context.original if context.original else {} else: og_item = context.original if context.original else {} curr_item = context.current if context.current else {} removed_keys = set(og_item.keys()) - set(curr_item.keys()) added_keys = set(curr_item.keys())- set(og_item.keys()) output = "{}:
".format(method_name) for k in curr_item.keys(): if k in og_item and og_item[k] != curr_item[k]: output += "key[{}], {} -> {}
".format(k, og_item[k], curr_item[k]) for add_k in added_keys: output += "key[{}], None -> {}
".format(add_k, curr_item[add_k]) for rem_k in removed_keys: output += "key[{}], {} -> None
".format(rem_k, og_item[rem_k]) LOG.debug(output) self._log_diff_call("create_network_precommit", context) self._log_diff_call("create_network_postcommit", context) self._log_diff_call("update_network_precommit", context) self._log_diff_call("update_network_postcommit", context) self._log_diff_call("delete_network_precommit", context) self._log_diff_call("delete_network_postcommit", context) self._log_diff_call("check_vlan_transparency", context) self._log_diff_call("create_subnet_precommit", context) self._log_diff_call("create_subnet_postcommit", context) self._log_diff_call("update_subnet_precommit", context) self._log_diff_call("update_subnet_postcommit", context) self._log_diff_call("delete_subnet_precommit", context) self._log_diff_call("delete_subnet_postcommit", context) self._log_diff_call("create_port_precommit", context) self._log_diff_call("create_port_postcommit", context) self._log_diff_call("update_port_precommit", context) self._log_diff_call("update_port_postcommit", context) self._log_diff_call("delete_port_precommit", context) self._log_diff_call("delete_port_postcommit", context) self._log_diff_call("bind_port", context)
