" % instance return str class KeepalivedManager(object): def __init__(self, resource_id, config, conf_path='/tmp', namespace=None, root_helper=None): self.resource_id = resource_id self.config = config self.namespace = namespace self.root_helper = root_helper self.conf_path = conf_path self.keepalived_pids = [] def _output_config_file(self): LOG.debug(_("Keepalived Configuration: %s"), self.config) config_path = self._get_conf_file_name('keepalived.conf') utils.replace_file(config_path, "%s" % self.config) return config_path def spawn(self): config_path = self._output_config_file() self.keepalived_pid = self._get_conf_file_name('keepalived.pid') self.checkers_pid = self._get_conf_file_name('checkers.pid') self.vrrp_pid = self._get_conf_file_name('vrrp.pid') cmd = ['keepalived', '-f', config_path, '--pid', self.keepalived_pid, '--cherkers_pid', self.checkers_pid, '--vrrp_pid', self.vrrp_pid] if self.namespace: ip_wrapper = ip_lib.IPWrapper(self.root_helper, self.namespace) ip_wrapper.netns.execute(cmd) else: utils.execute(cmd, self.root_helper) self.keepalived_pids = [self.keepalived_pid, self.checkers_pid, self.vrrp_pid] def _get_conf_file_name(self, kind, ensure_conf_dir=True): confs_dir = os.path.abspath(os.path.normpath(self.conf_path)) conf_dir = os.path.join(confs_dir, self.resource_id) if ensure_conf_dir: if not os.path.isdir(conf_dir): os.makedirs(conf_dir, 0o755) return os.path.join(conf_dir, kind) def restart(self): self._output_config_file() for pid_file in self.keepalived_pids: pid = self._read_pid(pid_file) if pid: cmd = ['kill', '-HUP', pid] utils.execute(cmd, self.root_helper) def kill(self): for pid_file in self.keepalived_pids: pid = self._read_pid(pid_file) if pid: cmd = ['kill', '-9', pid] utils.execute(cmd, self.root_helper) def _read_pid(self, file): try: with open(file, "r") as f: return f.readline().rstrip() except IOError: return

