 def test_start_bind_stats(self): listen_ip = '127.0.0.1' stats_port = '8053' client_ip = '127.0.0.1' content = ( 'statistics-channels {{
 inet {} port {} ' 'allow {{ {}; }};
}};'.format(listen_ip, stats_port, client_ip) ) self.patch_object( designate_bind.DesignateBindCharm.singleton, 'write_bind_file') designate_bind.start_bind_stats(listen_ip, stats_port) self.write_bind_file.assert_called_once_with( 'stats', content ) def test_end_bind_stats(self): self.patch_object( designate_bind.DesignateBindCharm.singleton, 'remove_bind_file') designate_bind.end_bind_stats() self.remove_bind_file.assert_called_once_with('stats') def test_write_bind_file(self): bind_file_name = "/etc/bind/stats.conf" self.patch_object( designate_bind.os.path, "join", return_value=bind_file_name ) a = designate_bind.DesignateBindCharm() with mock.patch("builtins.open", mock.mock_open()) as bob: a.write_bind_file("stats", "content") bob.assert_called_once_with(bind_file_name, "w") handle = bob() expected_calls = [ mock.call( "# The following header was added automatically by juju
" ), mock.call("# Modifying it will affect bind functionality
"), mock.call("content 
"), ] self.assertEqual(expected_calls, handle.write.call_args_list) self.assertTrue(bind_file_name in a.restart_map.keys()) def test_remove_bind_file(self): bind_file_name = '/etc/bind/stats.conf' a = designate_bind.DesignateBindCharm() self.patch_object( designate_bind.os.path, "join", return_value=bind_file_name ) self.patch_object(designate_bind.os.path, 'exists', return_value=True) self.patch_object(designate_bind.os, 'remove') a.restart_map[bind_file_name] = a.services self.assertTrue(bind_file_name in a.restart_map.keys()) a.remove_bind_file('stats') self.remove.assert_called_once_with(bind_file_name) self.assertTrue(bind_file_name not in a.restart_map.keys())
