t('template_rootfs_path', default='$share_export_root/template/rootfs', help='Template rootfs'), ] CONF = cfg.CONF CONF.register_opts(share_opts) uri = 'lxc:///' class LXCShareDriver(driver.ExecuteMixin, driver.ShareDriver): """Executes commands relating to Shares.""" def __init__(self, db, *args, **kwargs): """Do initialization.""" super(LXCShareDriver, self).__init__(*args, **kwargs) self.db = db self._helpers = None self.configuration.append_config_values(share_opts) def check_for_setup_error(self): """Returns an error if prerequisites aren't met.""" out, err = self._execute('vgs', '--noheadings', '-o', 'name', run_as_root=True) volume_groups = out.split() if self.configuration.share_volume_group not in volume_groups: msg = (_("share volume group %s doesn't exist") % self.configuration.share_volume_group) raise exception.InvalidParameterValue(err=msg) if not self.configuration.share_export_ip: msg = (_("share_export_ip doesn't specified")) raise exception.InvalidParameterValue(err=msg) def do_setup(self, context): """Any initialization the volume driver does while starting.""" super(LXCShareDriver, self).do_setup(context) try: self._conn = libvirt.open(uri) except Exception: LOG.error(_("An error occurred while trying to open connection: " "%s") % uri) raise self._setup_helpers() def _setup_helpers(self): """Initializes protocol-specific NAS drivers.""" self._helpers = {} for helper_str in self.configuration.share_lvm_helpers: share_proto, _, import_str = helper_str.partition('=') helper = importutils.import_class(import_str) #TODO(rushiagr): better way to handle configuration # instead of just passing to the helper self._helpers[share_proto.upper()] = helper(self._execute, self.configuration) def _local_path(self, share): # NOTE(vish): stops deprecation warning escaped_group = \ self.configuration.share_volume_group.replace('-', '--') escaped_name = share['name'].replace('-', '--') return "/dev/mapper/%s-%s" % (escaped_group, escaped_name) def _allocate_container(self, share_name, sizestr): cmd = ['lvcreate', '-L', sizestr, '-n', share_name, self.configuration.share_volume_group] if self.configuration.share_lvm_mirrors: cmd += ['-m', self.configuration.share_lvm_mirrors, '--nosync'] terras = int(sizestr[:-1]) / 1024.0 if terras >= 1.5: rsize = int(2 ** math.ceil(math.log(terras) / math.log(2))) # NOTE(vish): Next power of two for region size. See: # http://red.ht/U2BPOD cmd += ['-R', str(rsize)] self._try_execute(*cmd, run_as_root=True) def _deallocate_container(self, share_name): """Deletes a logical volume for share.""" # zero out old volumes to prevent data leaking between users # TODO(ja): reclaiming space should be done lazy and low priority try: self._try_execute('lvremove', '-f', "%s/%s" % (self.configuration.share_volume_group, share_name), run_as_root=True) except exception.ProcessExecutionError as exc: if "not found" not in exc.stderr: LOG.error(_("Error deleting volume: %s") % exc.stderr) raise LOG.error(_("Volume not found: %s") % exc.stderr) def get_share_stats(self, refresh=False): """Get share status. If 'refresh' is True, run update the stats first.""" if refresh: self._update_share_status() return self._stats def _update_share_status(self): """Retrieve status info from share volume group.""" LOG.debug(_("Updating share status")) data = {} # Note(zhiteng): These information are driver/backend specific, # each driver may define these values in its own config options # or fetch from driver specific configuration file. data["share_backend_name"] = 'LVM' data["vendor_name"] = 'Open Source' data["driver_version"] = '1.0' #TODO(rushiagr): Pick storage_protocol from the helper used. data["storage_protocol"] = 'NFS_CIFS' data['total_capacity_gb'] = 0 data['free_capacity_gb'] = 0 data['reserved_percentage'] = \ self.configuration.reserved_share_percentage data['QoS_support'] = False try: out, err = self._execute('vgs', '--noheadings', '--nosuffix', '--unit=G', '-o', 'name,size,free', self.configuration.share_volume_group, run_as_root=True) except exception.ProcessExecutionError as exc: LOG.error(_("Error retrieving volume status: %s") % exc.stderr) out = False if out: share = out.split() data['total_capacity_gb'] = float(share[1]) data['free_capacity_gb'] = float(share[2]) self._stats = data def deallocate_container(self, ctx, share): """Remove LVM volume that will be represented as share.""" self._deallocate_container(share['name']) def allocate_container(self, ctx, share): """Create LVM volume that will be represented as share.""" self._allocate_container(share['name'], '%sG' % share['size']) #create file system device_name = self._local_path(share) self._execute('mkfs.ext4', device_name, run_as_root=True) def allocate_container_from_snapshot(self, context, share, snapshot): """Is called to create share from snapshot.""" self._allocate_container(share['name'], '%sG' % share['size']) self._copy_volume(self._local_path(snapshot), self._local_path(share), snapshot['share_size']) def create_export(self, ctx, share): """Exports the volume. Can optionally return a Dictionary of changes to the share object to be persisted.""" device_name = self._local_path(share) location = self._mount_device(share, device_name) #TODO(rushiagr): what is the provider_location? realy needed? return {'provider_location': location} def remove_export(self, ctx, share): """Removes an access rules for a share.""" mount_path = self._get_mount_path(share) if os.path.exists(mount_path): #umount, may be busy try: self._execute('umount', '-f', mount_path, run_as_root=True) except exception.ProcessExecutionError, exc: if 'device is busy' in str(exc): raise exception.ShareIsBusy(share_name=share['name']) else: LOG.info('Unable to umount: %s', exc) #remove dir try: os.rmdir(mount_path) except OSError: LOG.info('Unable to delete %s', mount_path) def _get_domain_ip(self, domain, retry=1): """Recieves domain ip""" desc = etree.fromstring(domain.XMLDesc(0)) macAddr = desc.find("devices/interface[@type='network']/mac").\ attrib["address"].lower().strip() output = subprocess.Popen(["arp", "-n"], stdout=subprocess.PIPE).\ communicate()[0] lines = [line.split() for line in output.split("
")[1:]] IPaddr = [line[0] for line in lines if (line and (line[2] == macAddr))] max_retries = 3 if not IPaddr: if retry <= max_retries: LOG.debug("Can't retrieve IP, rebooting domain") self._restart_domain(domain) IPaddr = self._get_domain_ip(domain, retry + 1) else: raise exception.ManilaException("Can't get container IP") return IPaddr def _restart_domain(self, domain): try: domain.destroy() except Exception: LOG.debug('Trying to start domain') domain.create() time.sleep(10) def create_share(self, ctx, share): """Is called after allocate_space to create share on the volume.""" domain = self._get_domain(share['project_id']) domain_ip = self._get_domain_ip(domain)[0] LOG.debug("Domain IP is %s" % domain_ip) self._get_helper(share).setup_helper(domain_ip, self._get_lxc_path( share['project_id'])) location = self._get_helper(share).create_export(share['name'], domain_ip) return location def create_snapshot(self, context, snapshot): """Creates a snapshot.""" orig_lv_name = "%s/%s" % (self.configuration.share_volume_group, snapshot['share_name']) self._try_execute('lvcreate', '-L', '%sG' % snapshot['share_size'], '--name', snapshot['name'], '--snapshot', orig_lv_name, run_as_root=True) def ensure_share(self, ctx, share): """Ensure that storage are mounted and exported.""" device_name = self._local_path(share) self._mount_device(share, device_name) domain = self._get_domain(share['project_id']) domain_ip = self._get_domain_ip(domain)[0] LOG.debug("Domain IP is %s" % domain_ip) self._get_helper(share).setup_helper(domain_ip) self._get_helper(share).create_export(share['name'], domain_ip, recreate=True) def delete_share(self, ctx, share): """Delete a share.""" try: domain = self._get_domain(share['project_id']) domain_ip = self._get_domain_ip(domain)[0] LOG.debug("Domain IP is %s" % domain_ip) self._get_helper(share).setup_helper(domain_ip, self._get_lxc_path(share[ 'project_id'])) self._get_helper(share).remove_export(share['name'], domain_ip) except exception.ProcessExecutionError: LOG.info("Can't remove share %r" % share['id']) except exception.InvalidShare, exc: LOG.info(exc.message) def delete_snapshot(self, context, snapshot): """Deletes a snapshot.""" self._deallocate_container(snapshot['name']) def allow_access(self, ctx, share, access): """Allow access to the share.""" self._get_helper(share).allow_access(share['export_location'], share['name'], access['access_type'], access['access_to']) def deny_access(self, ctx, share, access): """Allow access to the share.""" self._get_helper(share).deny_access(share['export_location'], share['name'], access['access_type'], access['access_to']) def _get_helper(self, share): if share['share_proto'].startswith('NFS'): return self._helpers['NFS'] elif share['share_proto'].startswith('CIFS'): return self._helpers['CIFS'] else: raise exception.InvalidShare(reason='Wrong share type') def _mount_device(self, share, device_name): """Mount LVM share and ignore if already mounted.""" mount_path = self._get_mount_path(share) self._execute('mkdir', '-p', mount_path) try: self._execute('mount', device_name, mount_path, run_as_root=True, check_exit_code=True) self._execute('chmod', '777', mount_path, run_as_root=True, check_exit_code=True) except exception.ProcessExecutionError as exc: if 'already mounted' in exc.stderr: LOG.warn(_("%s is already mounted"), device_name) else: raise return mount_path def _get_mount_path(self, share): """Returns path where share is mounted.""" return os.path.join(self.configuration.share_export_root, share['project_id'], 'rootfs', share['name']) def _get_lxc_path(self, tenant_id): """Returns path where container fs will be created.""" return os.path.join(self.configuration.share_export_root, tenant_id, 'rootfs') def _copy_volume(self, srcstr, deststr, size_in_g): # Use O_DIRECT to avoid thrashing the system buffer cache extra_flags = ['iflag=direct', 'oflag=direct'] # Check whether O_DIRECT is supported try: self._execute('dd', 'count=0', 'if=%s' % srcstr, 'of=%s' % deststr, *extra_flags, run_as_root=True) except exception.ProcessExecutionError: extra_flags = [] # Perform the copy self._execute('dd', 'if=%s' % srcstr, 'of=%s' % deststr, 'count=%d' % (size_in_g * 1024), 'bs=1M', *extra_flags, run_as_root=True) def _get_xml(self, tenant_id): try: templ = open(CONF.lxc_xml_template).read() except Exception: LOG.error(_("An error occurred while trying to open xml: " "%s") % CONF.lxc_xml_template) raise container_rootfs = os.path.join(self.configuration.share_export_root, tenant_id, 'rootfs') return templ.replace('<name>lxc_template</name>', '<name>%s</name>' % tenant_id).\ replace("<source dir='/change_path'/>", "<source dir='/%s'/>" % container_rootfs) def _get_domain_by_name(self, instance_name): """Retrieve libvirt domain object given an instance name.""" try: return self._conn.lookupByName(instance_name) except libvirt.libvirtError: return None def _get_domain(self, tenant_id): domain = self._get_domain_by_name(tenant_id) if not domain: self._create_rootfs_for_domain(tenant_id) domain = self._create_domain(self._get_xml(tenant_id)) dom_state = domain.info()[0] if dom_state != libvirt.VIR_DOMAIN_RUNNING: LOG.debug("Domain is not running. Trying to start") domain.create() time.sleep(10) return domain def _create_domain(self, xml): """Create a domain.""" try: domain = self._conn.defineXML(xml) domain.create() time.sleep(30) except Exception as e: LOG.error(_("An error occurred while trying to define a domain" " with xml: %s") % xml) raise e return domain def _create_rootfs_for_domain(self, tenant_id): """Create a rootfs for domain from template.""" lxc_path = self._get_lxc_path(tenant_id) self._try_execute('mkdir', '-p', lxc_path) self._try_execute('cp', '-r', CONF.template_rootfs_path, os.path.join(lxc_path, '..'), run_as_root=True) self._try_execute('chmod', '774', lxc_path, run_as_root=True) class NASHelperBase(object): """Interface to work with share.""" def __init__(self, execute, config_object): self.configuration = config_object self._execute = execute def setup_helper(self, domain_ip, lxc_path): pass def create_export(self, share_name, domain_ip, recreate=False): """Create new export, delete old one if exists.""" raise NotImplementedError() def remove_export(self, share_name, domain_ip): """Remove export.""" raise NotImplementedError() def allow_access(self, export_location, share_name, access_type, access): """Allow access to the host.""" raise NotImplementedError() def deny_access(self, export_location, share_name, access_type, access, force=False): """Deny access to the host.""" raise NotImplementedError() class UNFSHelper(NASHelperBase): """Interface to work with share.""" def __init__(self, execute, config_object): super(UNFSHelper, self).__init__(execute, config_object) def setup_helper(self, domain_ip, lxc_path): try: self._execute('ssh', 'root@%s' % domain_ip, '-o StrictHostKeyChecking=no', 'unfsd') except Exception as e: if 'command not found' in str(e): raise exception.Exception('UNFS server not found') def create_export(self, share_name, domain_ip, recreate=False): """Create new export, delete old one if exists.""" return ':'.join([domain_ip, os.path.join('/', share_name)]) def remove_export(self, share_name, domain_ip): """Remove export.""" pass def allow_access(self, export_location, share_name, access_type, access): """Allow access to the host""" if access_type != 'ip': reason = 'only ip access type allowed' raise exception.InvalidShareAccess(reason) #check if presents in export domain_ip, local_share_path = export_location.split(':') out, _ = self._execute('ssh', 'root@%s' % domain_ip, 'showmount -e') out = re.search(re.escape(local_share_path) + '[\s
]*' + re.escape(access), out) if out is not None: raise exception.ShareAccessExists(access_type=access_type, access=access) self._execute('ssh', 'root@%s' % domain_ip, 'echo "%s %s(rw,no_subtree_check)" >> %s' % (local_share_path, access, '/etc/exports')) #(aostapenko)unfsd can't be restarted more civilized way self._execute('ssh', 'root@%s' % domain_ip, '-o StrictHostKeyChecking=no', 'killall -9 unfsd') time.sleep(1) self._execute('ssh', 'root@%s' % domain_ip, '-o StrictHostKeyChecking=no', 'unfsd') def deny_access(self, export_location, share_name, access_type, access, force=False): """Deny access to the host.""" domain_ip, local_share_path = export_location.split(':') out, _ = self._execute('ssh', 'root@%s' % domain_ip, 'cat /etc/exports') out = out.split('
