# vim: tabstop=4 shiftwidth=4 softtabstop=4 # Copyright 2013: Mirantis Inc. # All Rights Reserved. # # Licensed under the Apache License, Version 2.0 (the "License"); you may # not use this file except in compliance with the License. You may obtain # a copy of the License at # # http://www.apache.org/licenses/LICENSE-2.0 # # Unless required by applicable law or agreed to in writing, software # distributed under the License is distributed on an "AS IS" BASIS, WITHOUT # WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied. See the # License for the specific language governing permissions and limitations # under the License. import inspect import re from functools import partial def get_caller(): return inspect.stack()[2][-2][0] class Comparator(object): def __init__(self, symbol): self.value = symbol self.method = None self.print_symbol = None self.invert_print_symbol = None if symbol == '==': self._set_symbols('__eq__', '==', '!=') if symbol == '!=': self._set_symbols('__ne__', '!=', '==') if symbol == '>': self._set_symbols('__ne__', '!=', '==') def _set_symbols(self, method, print_symbol, invert_print_symbol): self.method = method self.print_symbol = print_symbol self.invert_print_symbol = invert_print_symbol def __str__(self): return 'method: {0}
 symbol: {1}
 invert_symbol: {2}
 value: {3}'\ .format(self.method, self.print_symbol, self.invert_print_symbol, self.value) class TestCase(object): def __init__(self): self._assertions = {} def _write_assertion(self, expr, msg): self._assertions[expr] = msg @classmethod def setup_class(cls): pass def setup(self): pass def test(self): pass def teardown(self): pass @classmethod def teardown_class(cls): pass def __getattr__(self, item): one_element_assertions = {'false': False, 'true': True, 'is_none': None} two_element_assertions = {'greater': '>', 'greater_equal': '>=', 'less': '<', 'less_equal': '<=', 'equal': '==', 'not_equal': '!='} item = item.replace('assert_', '') if item in one_element_assertions: comparator = Comparator(one_element_assertions[item]) return partial(self._one_element_assertion, comparator) if item in two_element_assertions: comparator = Comparator(two_element_assertions[item]) return partial(self._two_element_assertion, comparator) def _two_element_assertion(self, comparator, first, second, msg=''): if not getattr(first, comparator.method)(second): caller = get_caller() found = re.compile('\((.*),(.*)\)').search(caller).groups() exp = '{0} {1} {2}'.format(found[0], comparator.print_symbol, found[1].strip()) msg = msg or '{0} {1} {2}'.format(first, comparator.invert_print_symbol, second) self._write_assertion(exp, msg) def _one_element_assertion(self, comparator, expr, msg=''): if expr is not comparator.value: caller = get_caller() found = re.compile('\((.*)\)').search(caller).groups() exp = '{0} is {1}'.format(found[0], comparator) msg = msg or '{0} is not {1}'.format(expr, comparator) self._write_assertion(exp, msg)
