#!/bin/bash DEFAULT_GW=$1 ADMIN_IF=$(sed 's/\ /
/g' /proc/cmdline | grep choose_interface | awk -F\= '{print $2}') # Check if we do not already have static config (or interface seems unconfigured) NETADDR=( $(ifconfig $ADMIN_IF | grep -oP "[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}") ) if [ ! -z "$(grep $ADMIN_IF /etc/network/interfaces | grep dhcp)" ] ; then sed -i "/${ADMIN_IF}/d" /etc/network/interfaces echo 'include /etc/network/interfaces.d/*' >> /etc/network/interfaces echo -e "auto $ADMIN_IF
$ADMIN_IF inet static
	address ${NETADDR[0]}
	netmask ${NETADDR[2]}
	broadcast ${NETADDR[1]}
	gateway $DEFAULT_GW" > /etc/network/interfaces.d/ifcfg-"$ADMIN_IF" fi

