# does stdout support ansi? If so, updated progress status is displayed ANSI = hasattr(sys.stdout, "isatty") and sys.stdout.isatty() # for log output targets ROOT = "kolla" STDOUT = "stdout" STDERR = "stderr" def time_since_str(tm): return time.strftime("%H:%M:%S", time.gmtime(time.time()-tm)) class KollaBuildLogger(logging.Logger): """Logger adapted for per-image logging. Instances can get loggers and log following ways: - LOG.info(...), LOG.debug(...) -> using root logger - LOG(channel1, channel2, ..).info(...) -> using channel loggers """ def __init__(self): self.is_push = False self._log_level = logging.INFO self._loggers = dict() self._dir_name = None self._generate_self_logger() self._images = {} self._first_progress = True self._printing = threading.Lock() def __call__(self, *destinations): """Returns logger when calling LOG(dest1, dest2, ...) """ return self._get_logger(*destinations) def _generate_self_logger(self): """Sets logger for calling LOG.info(...) etc. """ lg = self._get_logger() for key, value in lg.__dict__.items(): setattr(self, key, value) def setLevel(self, lvl): """Sets logging level for all associated loggers. """ self._log_level = lvl super(KollaBuildLogger, self).setLevel(lvl) for logger in self._loggers.values(): logger.setLevel(lvl) def set_file_logging(self, dir_name=None): """Switches on file logging into dir_name directory. """ if dir_name is None: dir_name = 'logs-{}'.format(time.strftime('%Y%m%d%H%M%S', time.localtime())) try: os.makedirs(dir_name) except OSError as e: if e.errno != errno.EEXIST: raise self._dir_name = dir_name self._generate_self_logger() def is_file_logging(self): return self._dir_name is not None def _get_log_handler(self, name): if name == STDOUT: return logging.StreamHandler(sys.stdout) elif name == STDERR: return logging.StreamHandler(sys.stderr) else: return logging.FileHandler( os.path.join(self._dir_name, '{}.log'.format(name))) def _get_log_formatter(self, name, images): if self._log_level == logging.DEBUG and name not in {STDOUT, STDERR}: log_format_prefix = '[%(asctime)s][%(levelname)s]' \ '[%(name)s][%(filename)s][%(lineno)s]' \ '[%(funcName)s]' else: log_format_prefix = '' if name in {ROOT, STDOUT, STDERR} \ and len(images) > 0: formatter = logging.Formatter( '{}[{}]%(message)s'.format(log_format_prefix, ','.join(images))) else: formatter = logging.Formatter( '{}%(message)s'.format(log_format_prefix)) return formatter def _get_logger(self, *destinations): """Returns respective logger based on given destinations. Creates a new logger if not exists already. """ dest_names = set() if len(destinations) == 0: # if no explicit destinations - log to root logger dest_names.add(ROOT) if ROOT in destinations and not ANSI: # no ansi candies possible -> send root logger also to stdout dest_names.add(STDOUT) image_names = set() for dest in destinations: if isinstance(dest, dict): # destination is an image dict, get 'name' from it dest_names.add(dest['name']) image_names.add(dest['name']) else: # if not, channel must be string then dest_names.add(dest) if {STDOUT, STDERR} & dest_names: # any of them in dest_names? # make sure it logs also to root channel dest_names.add(ROOT) # channel name as combination of destinations # for identification/caching channel_name = '-'.join(dest_names) if not self.is_file_logging(): # no file logging - ensure stdout and # remove all other destinations # as they would need files dest_names.add(STDOUT) dest_names = dest_names & {STDOUT, STDERR} try: # try whether such channel exists already logger = self._loggers[channel_name] except KeyError: logger = logging.getLogger(channel_name) for dest_name in dest_names: fh = self._get_log_handler(dest_name) formatter = self._get_log_formatter(dest_name, image_names) fh.setFormatter(formatter) fh.setLevel(self._log_level) logger.addHandler(fh) logger.setLevel(self._log_level) self._loggers[channel_name] = logger return logger def record_status(self, image, status, log=True): """Records image status Output gous to ansi stdout (if it supports ansi) and to image loggers """ if image['status'] == 'unmatched': return self._printing.acquire() self._log_status(status, image) if status == 'rebuilding': status = 'building' self._images[image['name']] = status if log and ANSI and self._dir_name is not None: if not self._first_progress: for _ in range(self._dlines()): six.print_('\x1b[A\x1b[2K', end='', flush=True) self._first_progress = False six.print_('{}'.format(self._get_log_overview()), flush=True) self._printing.release() def _dlines(self): return 7 if self.is_push else 4 def _log_status(self, status, image): """Logs image status to respective log channels.""" if status == 'waiting': self._get_logger(image, ROOT).debug('Image identified for build') elif status == 'rebuilding': self._get_logger(image, ROOT).warning('Build failed, rebuilding') elif status == 'building': image['tm_start_build'] = time.time() self._get_logger(image, ROOT).info('Build started') elif status == 'built': self._get_logger(image, ROOT).info('Image built successfully') self._get_logger(image).debug( 'Build time: %s', time_since_str(image['tm_start_build'])) elif status == 'build_err': self._get_logger(image, ROOT).warning('Image build failed') elif status == 'pushing': image['tm_start_push'] = time.time() self._get_logger(image, ROOT).info('Push started') elif status == 'pushed': self._get_logger(image, ROOT).info( 'Image pushed into docker registry successfully') self._get_logger(image).debug( 'Push time: %s', time_since_str(image['tm_start_push'])) elif status == 'push_err': self._get_logger(image, ROOT).warning('Image push failed') def _present(self, status): """Presents status for ansi stdout disply.""" if status in ('parent_error',): status = 'built_err' sublist = [name for name, st in self._images.items() if st == status] ln = len(sublist) if ln == 0: ret = '' elif ln == 1: ret = sublist[0] elif ln == 2: ret = ', '.join(sublist) else: ret = '{} ({}, ...)'.format(ln, ', '.join(sublist[-2:])) if 'err' in status: # error - display in red ret = '\x1b[31m{}\x1b[0m'.format(ret) elif self.is_push and status == 'pushed' or \ not self.is_push and status == 'built': # finished - display in green ret = '\x1b[32m{}\x1b[0m'.format(ret) return ret def _get_log_overview(self): """Returns status overview for ansi stdout display.""" overview = 'Waiting : {}
Building : {}
