{}'.format(" ".join(command), rc, err)) if out == '': out = [] else: out = out.strip().split('
') return out def main(): module = AnsibleModule( argument_spec={ "filters": { "type": "list", "default": [] }, "targets": { "type": "list", "default": ["all"] }, "action": { "type": "str", "default": "inspect" }, "image": { "type": "str", "default": None }, "command": { "type": "str", "default": None }, "auto_remove": { "type": "bool", "default": True }, "mounts": { "type": "list", "default": [] }, }, supports_check_mode=True ) podman_bin = [module.get_bin_path('podman')] podman_facts = {} if podman_bin[0]: targets = module.params["targets"] if "all" in targets: targets.extend([t for t in PODMAN_SUBCOMMAND_LIST.keys() if t not in targets]) targets.remove("all") filters = itertools.chain.from_iterable( ['--filter', i] for i in module.params["filters"] ) action = [module.params["action"]] image = module.params["image"] command = module.params["command"] auto_remove = module.params["auto_remove"] if "rm" in action: action.append('-f') if "run" in action: if not command: module.fail_json(msg="No command specified when calling 'run'") if not image: module.fail_json(msg="No image specified when calling 'run'") if auto_remove: action.append("--rm") for mount in module.params["mounts"]: action.extend(["-v", mount]) action.append(image) action.extend(command.split()) action_out = run_podman_command( module, podman_bin, sub_command=action) podman_facts["output"] = action_out else: for target in targets: podman_facts[target] = {} ls = run_podman_command( module, podman_bin, sub_command=PODMAN_SUBCOMMAND_LIST[target], filters=filters) for item in ls: command = action + [item] action_out = run_podman_command( module, podman_bin, sub_command=command) podman_facts[target][item] = json.loads("
".join(action_out)) results = { "ansible_facts": { "validations_podman": podman_facts } } module.exit_json(**results) if __name__ == '__main__': main()
