#!/bin/awk -f BEGIN { CONTROLLER_CONSTANTS_FILE="CONTROLLER_CONSTANTS_FILE" in ENVIRON ? ENVIRON["CONTROLLER_CONSTANTS_FILE"] : "/opt/stack/dragonflow/dragonflow/controller/common/constants.py"; DATAPATH_ALLOCATION_FILE="DATAPATH_ALLOCATION_FILE" in ENVIRON ? ENVIRON["DATAPATH_ALLOCATION_FILE"] : "/var/run/dragonflow_datapath_allocation.json"; # id_to_name from python file cmd = "awk '/^[^#].*TABLE[\w]*/ {split($0, a, "\s*=\s*"); name=a[1]; id=a[2]; print id " " name}' " CONTROLLER_CONSTANTS_FILE while (cmd | getline) { split($0, a); id_to_name[ a[1] ] = a[2]; } close(cmd) # id_to_name from jq jq_code = "\ . as $all | 
\ def invert: . as $in |
\ $in | keys as $ks |
\ reduce $ks[] as $k ({}; .[$in[$k] | tostring] = $k);
\ def invert_and_merge: reduce . as $obj ({}; . |= $obj | invert);
\ def map_keys(f): . as $obj | $obj | keys as $ks | reduce $ks[] as $k ({}; .[$k | f] = $obj[$k]);
\ def on_exists($v2): . + "/" + $v2; 
\ def join($new): . as $orig | $new | keys as $n_ks |
\ reduce $n_ks[] as $k ($orig;
\ .[$k] = (if ($orig | has($k)) then ($orig[$k] | on_exists($new[$k])) else $new[$k] end));
\ def filter_keys(f): . as $obj | $obj | keys as $ks | reduce $ks[] as $k ({}; .[$k] = if ($k | f) then $obj[$k] else empty end);
\ $all | filter_keys(. != "dragonflow-legacy") as $all |
\ $all | keys as $k |
\ reduce $k[] as $var ({}; . += (
\ ($all[$var]["states"] | map_keys($var + "." + .) | invert)
\ | join(($all[$var]["entrypoints"] | map_keys($var + ".in." + .) | invert))
\ | join(($all[$var]["exitpoints"] | map_keys($var + ".out." + .) | invert))
\ ))
\ " cmd = "jq -f /dev/stdin " DATAPATH_ALLOCATION_FILE " << "EOF" | grep '"[0-9]*":\s*"[^"]*"' | sed 's/"\([0-9]*\)":\s*"\([^"]*\)",\?$/\1 \2/'
" jq_code "
EOF" while (cmd | getline) { split($0, a); id_to_name[ a[1] ] = a[2]; } close(cmd) } { head = "" tail=$0 while (match(tail, /(resubmit\(,|table=)([0-9]+)/, arr)) { repl = substr(tail, RSTART, RLENGTH) head = head substr(tail,1, RSTART-1) repl "(" id_to_name[arr[2]] ")" tail = substr(tail, RSTART+RLENGTH) } print head tail }
