from designate_tempest_plugin import data_utils as dns_data_utils from designate_tempest_plugin.common import waiters class ZonesImportOwnershipTest(BaseZonesImportTest): @classmethod def setup_clients(cls): super(ZonesImportOwnershipTest, cls).setup_clients() cls.client = cls.os.zone_imports_client cls.zones_client = cls.os.zones_client @test.attr(type='smoke') @test.idempotent_id('0bfd30fb-7e7e-4b06-8907-9f2bc929d4ae') def test_cannot_import_zone_with_record_outside_origin(self): name = dns_data_utils.rand_zone_name('testimport') altname = dns_data_utils.rand_zone_name('alttestimport') zonefile = ( "$ORIGIN {name}
" "$TTL {ttl}
" "{name} IN SOA ns.{name} nsadmin.{name} 101 102 103 104 105
" "{name} IN NS ns.{name}
" "ns.{name} IN A 127.0.0.1
" "{altname} IN A 127.0.0.1
" ).format(name=name, altname=altname, ttl=dns_data_utils.rand_ttl()) LOG.info('Try a zone import with a record name outside the origin') _, body = self.client.create_zone_import(zonefile) self.addCleanup(self.client.delete_zone_import, body['id']) LOG.info('Wait for the zone import to ERROR') waiters.wait_for_zone_import_status(self.client, body['id'], "ERROR") LOG.info('Ensure the failed zone import looks correct') _, zone_import = self.client.show_zone_import(body['id']) self.assertEqual("ERROR", zone_import['status']) self.assertEqual("Malformed zonefile.", zone_import['message']) self.assertIsNone(zone_import['zone_id']) self.assertIsNone(zone_import['links'].get('zone')) @test.attr(type="smoke") @test.idempotent_id('a6c5b244-f23e-4db4-ace5-5c2b2a713259') def test_import_zone_without_ttl_and_update_the_zone(self): name = dns_data_utils.rand_zone_name('testimport') zonefile = ( "$ORIGIN {name}
" "{name} IN SOA ns.{name} nsadmin.{name} 101 102 103 104 105
" "{name} IN NS ns.{name}
" "ns.{name} IN A 127.0.0.1
" ).format(name=name) LOG.info('Import a zone without any ttl') _, body = self.client.create_zone_import(zonefile) self.addCleanup(self.client.delete_zone_import, body['id']) LOG.info('Wait for the zone import to COMPLETE') waiters.wait_for_zone_import_status(self.client, body['id'], "COMPLETE") _, zone_import = self.client.show_zone_import(body['id']) LOG.info("Update the imported zone") # updating the zone with an empty json object seems to re-exercise the # api validation, which may cause the update to fail _, zone = self.zones_client._update_request( 'zones', zone_import['zone_id'], data={})

