".join(entries)) def format_dict(my_dict): return ''.join(['

 - **{}**: {}'.format(key, value) for key, value in six.iteritems(my_dict)]) def build_detail(group, validations): entries = ['{}
{}
'.format(group, len(group) * '=')] entries = entries + [ """.. _{label}: {title} {adornment} {name}. {desc} - **hosts**: {hosts} - **groups**: {groups} - **metadata**: {metadata} - **parameters**: {parameters} `View validation source code <http://git.openstack.org/cgit/openstack/tripleo-validations/plain/validations/{title}.yaml>`__. """ .format(label=(group + '_' + validation['id']), title=validation['id'], adornment=(len(validation['id']) * '-'), name=validation['name'], desc=validation['description'], groups=', '.join(validation['groups']), metadata=format_dict(validation['metadata']), hosts=validation['hosts'], parameters=format_dict(validation['parameters']), ) for validation in validations] with open('doc/source/validations-{}-details.rst'.format(group), 'w') as f: f.write("
".join(entries)) def setup(app): # Seed it with the known groups: groups = set(('prep', 'pre-introspection', 'pre-deployment', 'post-deployment')) validations = [] for validation_path in sorted(glob('validations/*.yaml')): with open(validation_path) as f: loaded_validation = yaml.safe_load(f.read())[0] for group in get_validation_metadata(loaded_validation, 'groups'): groups.add(group) validations.append({ 'hosts': loaded_validation['hosts'], 'parameters': get_validation_parameters(loaded_validation), 'id': os.path.splitext( os.path.basename(validation_path))[0], 'name': get_validation_metadata(loaded_validation, 'name'), 'groups': get_validation_metadata(loaded_validation, 'groups'), 'description': get_validation_metadata(loaded_validation, 'description'), 'metadata': get_remaining_metadata(loaded_validation) }) for group in groups: validations_in_group = [validation for validation in validations if group in validation['groups']] build_detail(group, validations_in_group) build_summary(group, validations_in_group)
