#{nodes_uids.pretty_inspect}
finished " \ "with errors. Nodes
#{answer['inaccessible_nodes'].pretty_inspect}
are inaccessible" end if error_nodes.present? answer.merge!({'status' => 'error', 'error_nodes' => to_report_format(error_nodes, nodes)}) Astute.logger.error "#{ctx.task_id}: Running shell command on nodes
#{nodes_uids.pretty_inspect}
finished " \ "with errors:
#{answer['error_nodes'].pretty_inspect}" end Astute.logger.info "#{ctx.task_id}: Finished running shell command:
#{nodes_uids.pretty_inspect}" answer end private def self.to_report_format(slave_names, nodes) result_nodes = nodes.select { |n| slave_names.include?(n['admin_ip']) } result_nodes.inject([]) do |result, node| result << {'uid' => node['uid']} if node['uid'] result end end def self.run_remote_command(nodes, cmd, timeout) servers = [] channel = nil Net::SSH::Multi.start(:concurrent_connections => Astute.config.max_nodes_per_call, :on_error => :warn) do |session| nodes.each do |name| session.use name, :user => 'root', :host_key => 'ssh-rsa', :keys => ['/root/.ssh/id_rsa'] end servers = session.servers_for # execute commands on all servers # FIXME: debug not show a messages if command contain a several # strings channel = session.exec cmd do |ch, success| ch.on_data do |ichannel, data| Astute.logger.debug "[#{ch[:host]} : #{ichannel}] #{data}" end ch.on_request "exit-status" do |_ichannel, data| exit_status = data.read_long end end Timeout::timeout(timeout) { session.loop } end detect_status(servers) rescue Timeout::Error Astute.logger.debug "SSH session is closed due to the achievement of a timeout" return [[], [], nodes] unless servers exception_process(servers) rescue Net::SSH::Disconnect Astute.logger.debug "SSH connection closed by remote host" exception_process(servers) end def self.exception_process(servers) servers.each do |s| if s.busy? # Pending connection could not be shutdown, but always return busy as true s.session.shutdown! if s.session.channels.present? s.fail! end end detect_status(servers) end # TODO: support exit code from shell command def self.detect_status(servers) executed_nodes = [] inaccessible_nodes = [] servers.each do |s| s.failed? ? inaccessible_nodes << s.host : executed_nodes << s.host end [executed_nodes, [], inaccessible_nodes] end end end
