 def max_body_size(resp): """Validates if the resp body size is more than max size in the config Checks for response body to see if the size is more than given size in config. If it is, returns a Signal. :returns: SynSignal or None """ data = { "req": resp.request, "resp": resp, "req_len": len(resp.request.body or ""), "resp_len": len(resp.content or ""), } text = ( "Length:
" "	Request length: {0}
" "	Response length: {1}
".format( data["req_len"], data["resp_len"] ) ) slug = "OVER_MAX_LENGTH" if data["resp_len"] > CONF.test.max_length: "Response length is larger than configured/expected max length" return syntribos.signal.SynSignal( text=text, slug=slug, strength=1, data=data)
