# Copyright 2016 Mirantis, Inc. # # Licensed under the Apache License, Version 2.0 (the "License"); you may # not use this file except in compliance with the License. You may obtain # a copy of the License at # # http://www.apache.org/licenses/LICENSE-2.0 # # Unless required by applicable law or agreed to in writing, software # distributed under the License is distributed on an "AS IS" BASIS, WITHOUT # WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied. See the # License for the specific language governing permissions and limitations # under the License. import os from fuelweb_test import settings from fuelweb_test.settings import iface_alias def prepare_steps(): cloud_image_dir_path = "{}/cloudimage".format(os.getcwd()) if not os.path.exists(cloud_image_dir_path): os.makedirs(cloud_image_dir_path) meta_data_path = "{}/meta_data".format(cloud_image_dir_path) user_data_path = "{}/user_data".format(cloud_image_dir_path) return cloud_image_dir_path, meta_data_path, user_data_path def generate_meta_data(d_env, meta_data_path): admin_net_object = d_env.get_network(name=d_env.admin_net) admin_network = admin_net_object.ip.network admin_netmask = admin_net_object.ip.netmask admin_ip = str(d_env.nodes( ).admin.get_ip_address_by_network_name(d_env.admin_net)) context = { "interface_name": "{}".format(iface_alias("eth0")), "address": "{}".format(admin_ip), "network": "{}".format(admin_network), "netmask": "{}".format(admin_netmask), "gateway": "{}".format(d_env.router()), "dns": "{}".format(settings.DNS), "dns_ext": "{}".format(settings.EXTERNAL_DNS) } meta_data_content = ("" "instance-id: iid-local1
" "network-interfaces: |
" " auto {interface_name}
" " iface {interface_name} inet static
" " address {address}
" " network {network}
" " netmask {netmask}
" " gateway {gateway}
" " dns-nameservers {dns} {dns_ext}
" "local-hostname: pxeserver.local") with open(meta_data_path, 'a') as f: f.write(meta_data_content.format(**context)) def generate_user_data(d_env, user_data_path): context = { "interface_name": "{}".format(iface_alias("eth0")), "gateway": "{}".format(d_env.router()) } user_data_content = ("" "#cloud-config
" "ssh_pwauth: True
" "chpasswd:
" "list: |
" " root:r00tme
" "expire: False 

" "runcmd:
" " - sudo ifup {interface_name}
" " - sudo sed -i -e '/^PermitRootLogin/s/^" ".*$/PermitRootLogin yes/' /etc/ssh/sshd_config
" " - sudo service ssh restart
" " - sudo route add default gw " "{gateway} {interface_name}") with open(user_data_path, 'a') as f: f.write(user_data_content.format(**context)) def generate_cloudimage_iso(cloud_image_dir_path, user_data_path, meta_data_path): cmd = "genisoimage -output {}/cloud_centos.iso -volid cidata " \ "-joliet -rock {} {}".format(cloud_image_dir_path, user_data_path, meta_data_path) os.system(cmd)

